import{_ as a,o as t,c as i,ai as r}from"./chunks/framework.ChYKzJpA.js";const f=JSON.parse('{"title":"About","description":"","frontmatter":{"title":"About","outline":"deep","sidebar":false},"headers":[],"relativePath":"en/about/index.md","filePath":"en/about/index.md"}'),o={name:"en/about/index.md"};function n(l,e,s,c,d,h){return t(),i("div",null,[...e[0]||(e[0]=[r('<h1 id="about" tabindex="-1">About <a class="header-anchor" href="#about" aria-label="Permalink to “About”">​</a></h1><p>Security researcher focused on IoT, vehicle connectivity, and mobile security. I pay special attention to risks that have direct real-world impact—e.g., abuse of vehicle control links, or theft and fraud in payment scenarios. My work is practical and engineering-driven: build reproducible attack chains to verify the true risk boundary, pinpoint root causes, propose actionable fixes, and drive vendors through remediation and retesting.</p><p>I’m currently exploring <strong>AI for Security</strong>: how to use AI to boost security research and defense efficiency. The goal is to make frontier technology serve a reliable and controllable real world.</p><h2 id="goals" tabindex="-1">Goals <a class="header-anchor" href="#goals" aria-label="Permalink to “Goals”">​</a></h2><ul><li>Drive real fixes with offense/defense: use reproducible attacks to validate real risks, and push vendors to fix and retest.</li><li>Make intelligence safer: as AI permeates vehicles, homes, and critical systems, study new attack surfaces and verifiable security baselines.</li><li>Build with long-term responsibility: practice responsible disclosure and long-termism, keep doing useful security research so more people benefit from technology.</li></ul><h2 id="some-highlights" tabindex="-1">Some Highlights <a class="header-anchor" href="#some-highlights" aria-label="Permalink to “Some Highlights”">​</a></h2><ul><li><a href="/blog/en/contributions/">Talks &amp; contributions</a></li><li><a href="/blog/en/contributions/#cves">Vulnerabilities &amp; acknowledgements</a></li><li><a href="/blog/en/contributions/#awards">Competitions &amp; awards</a></li></ul><h2 id="contact" tabindex="-1">Contact <a class="header-anchor" href="#contact" aria-label="Permalink to “Contact”">​</a></h2><ul><li>GitHub: <a href="https://github.com/migraine-sudo" target="_blank" rel="noreferrer">https://github.com/migraine-sudo</a></li><li>X (Twitter): <a href="https://x.com/0xp0kerface" target="_blank" rel="noreferrer">https://x.com/0xp0kerface</a></li><li>Gmail: <a href="mailto:0xp0kerface@gmail.com" target="_blank" rel="noreferrer">0xp0kerface@gmail.com</a></li></ul>',9)])])}const m=a(o,[["render",n]]);export{f as __pageData,m as default};
